package OopRevise;

public class Revise {
	
	//declare an attribute 
	int x;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//create a new instance of Revise
		Revise revise1 = new Revise();
		//set the value of x
		revise1.x = 40;
		System.out.println(revise1.x);
		
		
		//create another instance of Revise with a different value for the variable x
		Revise revise2 = new Revise();
		//set the value of x
		revise2.x = 60;
		System.out.println(revise2.x);
		

	}

}
